operation.search:
// Set up the inputs for the form.
$form_subject = strtr($smcFunc['htmlspecialchars']($_REQUEST['subject']), array("\r" => '', "\n" => '', "\t" => ''));
$form_message = $smcFunc['htmlspecialchars']($_REQUEST['message'], ENT_QUOTES);

operation.add:
//Don't use nasty language, please...
if (!empty($modSettings['block_censored_words'])) {
	$check_censor = false;
	$aux = $form_subject;
	censorText($aux);
	if (strcmp($aux, $form_subject) != 0) {
		$check_censor = true;	
	}
	if (!$check_censor) {
		$aux = $form_message;
		censorText($aux);
		if (strcmp($aux, $form_message) != 0) {
			$check_censor = true;
		}
	}
	if ($check_censor)
		$context['post_error']['bad_words'] = true;
	unset($aux);
	unset($check_sensor);
}

operation.search:
// If the user isn't a guest, get his or her name and email.
elseif (!isset($_REQUEST['msg']))
{
	$_POST['guestname'] = $user_info['username'];
	$_POST['email'] = $user_info['email'];
}

operation.add:
//Check for nasty words when submitting a message
if (!empty($modSettings['block_censored_words'])) {
	$check_censor = false;
	$aux = $_POST['subject'];
	censorText($aux);
	if (strcmp($aux, $_POST['subject']) != 0) {
		$check_censor = true;	
	}
	if (!$check_censor) {
		$aux = $_POST['message'];
		censorText($aux);
		if (strcmp($aux, $_POST['message']) != 0) {
			$check_censor = true;	
		}		
	}
	if ($check_censor)
		$post_errors[] = 'bad_words';
	unset($aux);
	unset($check_censor);
}
